import axios from 'axios';
import React, { useState } from 'react'
import { withRouter } from 'react-router-dom'
//history burqakıdakı componentlere gondermemız ıcın props ıle bu durumu yaprız bunu with navigation ıle yaparımz bunun ıcın bunu ımport ederız
//ve export ıle bu componentı sararız.
function YaziFormu(props) {
  const [yazi, setYazi] = useState({ title: "", content: "" });
  const [hata, setHata] = useState("");
  const onInputChange = (event) => setYazi({ ...yazi, [event.target.name]: event.target.value });
  const onFormSubmit = (event) => {
    event.preventdefault();
    setHata("");
    axios.post('https://react-yazi-yorum.herokuapp.com/posts/', yazi)
      .then(response => {
        //basarılıysa
        props.history.push('/');
      })
      .catch(error => {
        setHata("Başlık ve yazı içeriği alanları zorunlduur.")
      })
  };
  return (
    <>
    <div style={{ width: "500px", marginLeft: "40%", marginTop: "10%" }}>
      <h2>YaziEkle</h2>
      <div class="ui form error">
        {hata && (
           <div class="ui error message">
          <div class="header">Hata</div>
          <p>{hata} </p>
        </div>
        )}

        <div class="ui submit button">Submit</div>
      </div>
      <form >
        <div class="form-group">
          <label for="exampleFormControlInput1">Yazı Başlığı</label>
          <input type="email" class="form-control" id="exampleFormControlInput1" name="title" onChange={onInputChange} />
        </div>

        <div class="form-group">
          <label for="exampleFormControlTextarea1" className="mr-3">Yazı İçeriği</label>
          <textarea class="form-control" id="exampleFormControlTextarea1" rows="5" name="content" onChange={onInputChange}  ></textarea>
          <button type="button" class="btn btn-primary mr-2 mt-3" onClick={onFormSubmit}>Gönder</button>
          <button type="button" class="btn btn-dark mt-3">İptal Et</button>

        </div>
      </form>
    </div>
    </>
  )
}

export default withRouter(YaziFormu)
