import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

import YorumListesi from "./YorumListesi";


const YaziDetayi = () => {
  const { id } = useParams()
  const [yaziDetayi, setYaziDetayi] = useState({});

  // const [display_name, setDisplay_name] = useState('');
  // const [body, setBody] = useState('');

  const [a, setA] = useState("xd");



  useEffect(() => {

    axios
      .get(`https://react-yazi-yorum.herokuapp.com/posts/${id}`)
      .then((response) => {
        setYaziDetayi(response.data);
      })
      .catch((error) => {
        console.log(error);
      });

  }, [id]);

  useEffect(() => {
    console.log('a', a)
  }, [])

  return (
    <React.Fragment>
      <div className="ui raised very padded text container segment">
        <h2 className="ui header">{yaziDetayi.title} </h2>
        <p className="header">{yaziDetayi.created_at} </p>
        <p className="header">{yaziDetayi.content} </p>
      </div>

      <YorumListesi/>

      <button onClick={() => setA(Math.random() * 5000)} className="btn btn-primary">xdxd</button>
    </React.Fragment>
  );
};
export default YaziDetayi
