import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";


const YaziDetayi = () => {

  const [yaziDetayi, setYaziDetayi] = useState({});
  const [yorumlar, setYorumlar] = useState([]);
  // const [display_name, setDisplay_name] = useState('');
  // const [body, setBody] = useState('');
  const [yorum, setyorum] = useState({ display_name: "", body: "" })
  const handleCommentSubmit = yorum => {
    axios.post(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`, { yorum })
    .then((response)=>{
      setYorumlar(response.data);
    }
      ) //ikinic yer verit abnaına kaydetmemızı ıstedıgım obje
  }
  const handleOnChange=event=>{
    setyorum({...yorum,[event.target.name]:event.target.value}) //yorum kaydedılıcek daha oncekı degerler sılınmıcek
  }
  const { id } = useParams()
  console.log(id);

  useEffect(() => {

    axios.get(`https://react-yazi-yorum.herokuapp.com/posts/${id}`)
      .then((response) => {
        setYaziDetayi(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
    axios
      .get(`https://react-yazi-yorum.herokuapp.com/posts/${id}/comments`) //yorumları getırıdk
      .then((response) => {
        setYorumlar(response.data);
      })

  });
  return (
    <React.Fragment>
      <div className="ui raised very padded text container segment">
        <h2 className="ui header">{yaziDetayi.title} </h2>
        <p className="header">{yaziDetayi.created_at} </p>
        <p className="header">{yaziDetayi.content} </p>
      </div>
      <h3>Yorumlar</h3>
      {yorumlar.map(yorum => {
        return (
          <div className="ui relaxed list" key={yorum.id}>
            <div className="item">
              <img className="ui avatar image" src="/images/avatar/small/daniel.jpg" />
              <div className="content">
                <a className="header">
                  {yorum.display_name}
                </a>
                <div className="description">{yorum.body} </div>
              </div>
            </div>

          </div>
        )
      })}
      <h3>Yorum Yaz</h3>
      <form class="ui form " onSubmit={handleCommentSubmit}> <div class="ui mini icon input">
        <input type="text" placeholder="Adınız"name="yorum.display_name" onChange={handleOnChange} />
         {/* //yorum ıcındekı body */}
        <i class="search icon"></i>
      </div>

        <textarea placeholder='Yorumumuz' rows="3" name="body"  onChange={handleOnChange} value={yorum.body} ></textarea>
        <button className="ui blue button" type="submit">
          Gönder
        </button>
      </form>
    </React.Fragment>



  );
};
export default YaziDetayi
